DROP TABLE IF EXISTS users CASCADE;
-- 1. Users
CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    name VARCHAR NOT NULL,
    email VARCHAR UNIQUE NOT NULL,
    phone VARCHAR,
    password_hash VARCHAR NOT NULL,
    role VARCHAR CHECK (role IN ('customer', 'admin')) DEFAULT 'customer'
);

DROP TABLE IF EXISTS cinemas CASCADE;
-- 2. Cinemas
CREATE TABLE cinemas (
    cinema_id SERIAL PRIMARY KEY,
    name VARCHAR NOT NULL,
    address TEXT,
    city VARCHAR,
    phone VARCHAR,
    latitude DECIMAL(10,8),
    longitude DECIMAL(11,8)
);
-- Drop table if exists
DROP TABLE IF EXISTS movies CASCADE;

-- Create table movies
CREATE TABLE movies (
    movie_id SERIAL PRIMARY KEY,
    title VARCHAR,
    description TEXT,
    duration INT,
    release_date DATE,
    language VARCHAR,
    genre VARCHAR,
    poster_url VARCHAR,
    trailer_url VARCHAR,
    rating VARCHAR
);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('THANH GƯƠM DIỆT QUỶ: VÔ HẠN THÀNH', 'Bộ phim đầu tiên trong bộ ba phim Thanh Gươm Diệt Quỷ: Vô Hạn Thành. Đây là phần bắt đầu cho trận chiến cuối cùng giữa Sát Quỷ Đoàn cùng Muzan và bè lũ quỷ tại Vô Hạn Thành. Bộ phim quy tụ gần như toàn bộ Trụ Cột & các nhân vật chính đối đầu nhóm Thượng Huyền mạnh nhất của Muzan; cùng bối cảnh Vô Hạn Thành phức tạp, kiến trúc đảo lộn và di chuyển liên tục hứa hẹn mang đến những trận chiến đấu đầy mãn nhãn.', 155, '2025-08-15', 'Tiếng Nhật - Phụ đề Tiếng Việt & Tiếng Anh', 'Hành Động, Hoạt Hình', 'img/demon-slayer-infinity-castle/350x495-kny.jpg', NULL, 'T16 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 16 TUỔI TRỞ LÊN (16+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('MANG MẸ ĐI BỎ', 'Mang Mẹ Đi Bỏ kể về số phận của Hoan (Tuấn Trần) - một chàng trai trẻ ngày ngày hóa thân thành “thằng hề đường phố” với ngón nghề cắt tóc vỉa hè để kiếm tiền lo cho mẹ. Mẹ Hoan là bà Lê Thị Hạnh (Hồng Đào), mắc bệnh Alzheimer và cư xử như một đứa trẻ con. Căn bệnh của mẹ không chỉ là gánh nặng mưu sinh, mà còn lấy đi của Hoan một cuộc đời tự do với những ước mơ chưa thể thực hiện. Một ngày, Hoan quyết định mang mẹ “đi bỏ” cho người anh trai ở Hàn Quốc, người mà chính Hoan còn không biết mặt mũi ra sao.', 112, '2025-08-01', 'Tiếng Việt & Tiếng Hàn - Phụ đề tiếng Anh', 'Gia đình, Tâm Lý', 'img/mang-me-di-bo/mm_b_poster_teaser2_fa_70x100cm_digital_1_.jpg', NULL, 'K - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM DƯỚI 13 TUỔI VÀ CÓ NGƯỜI BẢO HỘ ĐI KÈM');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('PHIM ĐIỆN ẢNH THÁM TỬ LỪNG DANH CONAN: DƯ ẢNH CỦA ĐỘC NHÃN', 'Trên những ngọn núi tuyết của Nagano, một vụ án bí ẩn đã đưa Conan và các thám tử quay trở lại quá khứ. Thanh tra Yamato Kansuke - người đã bị thương nặng trong một trận tuyết lở nhiều năm trước - bất ngờ phải đối mặt với những ký ức đau thương của mình trong khi điều tra một vụ tấn công tại Đài quan sát Nobeyama. Cùng lúc đó, Mori Kogoro nhận được một cuộc gọi từ một đồng nghiệp cũ, tiết lộ mối liên hệ đáng ngờ giữa anh ta và vụ án đã bị lãng quên từ lâu. Sự xuất hiện của Morofushi Takaaki, cùng với những nhân vật chủ chốt như Amuro Tooru, Kazami và cảnh sát Tokyo, càng làm phức tạp thêm cuộc điều tra. Khi quá khứ và hiện tại đan xen, một bí ẩn rùng rợn dần dần được hé lộ - và ký ức của Kansuke nắm giữ chìa khóa cho mọi thứ.', 109, '2025-07-25', 'Tiếng Nhật - Phụ đề Tiếng Việt; Lồng Tiếng Việt', 'Bí ẩn, Hành Động, Hoạt Hình', 'img/conan-2025/1920x1080_up.png', NULL, 'K - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM DƯỚI 13 TUỔI VÀ CÓ NGƯỜI BẢO HỘ ĐI KÈM');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('BỘ TỨ SIÊU ĐẲNG: BƯỚC ĐI ĐẦU TIÊN', 'Sau một chuyến bay thám hiểm vũ trụ, bốn phi hành gia bất ngờ sở hữu năng lực siêu nhiên và trở thành gia đình siêu anh hùng đầu tiên của Marvel. The Fantastic Four: First Steps là bộ phim mở đầu Kỷ nguyên anh hùng thứ sáu (Phase Six), đặt nền móng cho siêu bom tấn Avengers: Doomsday trong năm sau.', 115, '2025-07-25', 'Tiếng Anh - Phụ đề Tiếng Việt; Lồng tiếng Việt', 'Hành Động, Khoa Học Viễn Tưởng, Phiêu Lưu', 'img/the-fantastic-4/cgv_350x495.jpg', NULL, 'T13 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 13 TUỔI TRỞ LÊN (13+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('ZOMBIE CƯNG CỦA BA', 'Zombie Cưng Của Ba đánh dấu sự trở lại của nam diễn viên Cho Jung Seok trên màn ảnh rộng năm nay. Cho Jung Seok hóa thân thành Lee Jung Hwan, một huấn luyện viên động vật đầy nhiệt huyết quyết tâm bảo vệ cô con gái tuổi teen bị nhiễm virus zombie bằng cách “thuần hóa” cô bé. Trải qua hành trình Jung Hwan đưa cô con gái nhiễm virus zombie Soo Ah đến ngôi làng ven biển, với nỗ lực bảo vệ cô bé khỏi sự truy quét của chính quyền. Tại đây, anh phát hiện Soo Ah vẫn còn giữ được ý thức con người, đặc biệt là niềm đam mê nhảy múa. Ngoài ra, cô bé vẫn nhận ra và vô cùng yêu thương bà của mình. Từ đó, Jung Hwan đưa ra quyết định quan trọng, đó là áp dụng kinh nghiệm huấn luyện động vật của chính mình để “thuần hóa” con gái, mở ra cuộc hành trình vừa hài hước vừa cảm động. Zombie Cưng Của Ba có đầy đủ yếu tố trở thành “bữa tiệc hè” đặc sắc từ xứ kim chi dịp hè 2025 khi đầy đủ các yếu tố cảm xúc HÀI - TÌNH - LỆ', 107, '2025-08-08', 'Tiếng Hàn – Phụ đề Tiếng Việt; Lồng Tiếng Việt - Phụ đề tiếng Anh', 'Gia đình, Hài', 'img/zombie-cung-cua-ba/1080wx608h-zombie.jpg', NULL, 'T13 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 13 TUỔI TRỞ LÊN (13+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TOÀN TRÍ ĐỘC GIẢ', 'Khi thế giới diệt vong trong cuốn tiểu thuyết yêu thích bỗng biến thành hiện thực, Kim Dokja (Ahn Hyo-seop) - 1 nhân viên văn phòng bình thường cũng là người duy nhất biết được kết truyện phải bắt tay vào cuộc hành trình đến phân cảnh cuối cùng. Cùng những đồng đội ngẫu nhiên và người hùng Yu Jeong-hyeok (Lee Min-ho) tập hợp thành 1 nhóm, Kim Dokja cần vượt qua mọi thử thách để sống sót trong thế giới bộ truyện mà anh yêu thích.', 116, '2025-08-01', 'Tiếng Hàn - Phụ đề Tiếng Việt', 'Hành Động, Thần thoại', 'img/toan-tri-doc-gia/350x495-omni.jpg', NULL, 'T16 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 16 TUỔI TRỞ LÊN (16+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('CHỐT ĐƠN!', 'Ông An là một người giao hàng đang tìm kiểm cha mẹ của một đửa trẻ bị bỏ rơi. Ông tình cờ gặp Linh, một nữ hoàng livestream, đang mang gánh nặng công việc và áp lực gia đình. Giữa những mơ hồ, thay đổi, áp lực của xã hội, Linh và ông An dân cảm thông và giúp đỡ lẫn nhau, khởi tạo nên những nhiệt huyết mới để mang đến những phiên "Chốt đơn!" thẳng đậm.', 112, '2025-08-08', 'Tiếng Việt - Phụ đề Tiếng Anh', 'Hài, Tâm Lý', 'img/chot-don/350x495-chotdon.jpg', NULL, 'T16 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 16 TUỔI TRỞ LÊN (16+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('GIỜ THẢ MÁU', 'Gã thuyền trưởng cuồng cá mập bắt cóc các nạn nhân trẻ để biến họ thành mồi đàn cá, hắn ghi lại những khoảnh khắc tuyệt vọng của nạn nhân để hưởng thụ. Trên con thuyền lênh đênh giữa đại dương, không ai nghe thấy tiếng kêu cứu, họ phải đấu với gã thuyền trưởng điên loạn nếu không muốn trở thành thức ăn cho cá mập.', 98, '2025-08-08', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Hồi hộp, Kinh Dị', 'img/dangerous-animal/350x495-camap.jpg', NULL, 'T18 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 18 TUỔI TRỞ LÊN (18+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('JUMBO VÀ NGƯỜI BẠN DIỆU KỲ', 'Don, một cậu bé mồ côi 10 tuổi, bị đánh giá thấp vì ngoại hình to lớn, quyết tâm tham gia cuộc thi tài năng để chứng minh giá trị bản thân. Nhờ cuốn truyện tranh kỳ diệu do cha mẹ để lại và cuộc gặp gỡ cô bé Meri, Don bước vào hành trình khám phá lòng can đảm, tình bạn và giá trị bản thân. Cả hai cùng vượt qua nhiều thử thách để tìm lại cuốn truyện bị đánh cắp và giúp Meri hội ngộ với cha mẹ mình. Hành trình ấy đưa Don đến sân khấu chính, nơi cậu dũng cảm đứng lên thể hiện bản thân và chia sẻ câu chuyện của mình với mọi người.', 102, '2025-08-08', 'Lồng tiếng Việt - Phụ đề Tiếng Anh', 'Hoạt Hình', 'img/jumbo-nguoi-ban-dieu-ky/poster_jumbo_1080x1350.jpg', NULL, 'P - PHIM ĐƯỢC PHÉP PHỔ BIẾN ĐẾN NGƯỜI XEM Ở MỌI ĐỘ TUỔI.');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('HỌNG SÚNG VÔ HÌNH', 'Chỉ có một người đàn ông sở hữu bộ kỹ năng đặc biệt... để lãnh đạo Đội Cảnh sát và cứu thế giới! Trung úy Frank Drebin Jr. (do Liam Neeson thủ vai) nối tiếp bước cha mình trong Họng Súng Vô Hình, do Akiva Schaffer đạo diễn và Seth MacFarlane đảm nhận vai trò sản xuất. Tham gia vụ án này còn có dàn diễn viên Pamela Anderson, Paul Walter Hauser, CCH Pounder, Kevin Durand, Cody Rhodes, Liza Koshy, Eddie Yu, cùng Danny Huston.', 85, '2025-08-01', 'Tiếng Anh - phụ đề Tiếng Việt', 'Hài, Hành Động', 'img/naked-gun/1800x1200-nakedgun.jpg', NULL, 'T16 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 16 TUỔI TRỞ LÊN (16+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('MA LÒNG THÒNG', 'Sau khi nhận tin cha qua đời tại làng Kidul, Ryan quyết trở về chịu tang dù bị mẹ – bà Marlina, kiên quyết ngăn cản. Anh cùng Alana, Ben và Elsa lên đường, không ngờ đang bước vào một nơi bị nguyền rủa. Ngôi làng bị ám bởi Pulung Gantung – bóng ma rình rập trong đêm, gieo rắc lời nguyền khiến người sống dần mất kiểm soát và tự treo cổ kết liễu đời mình. Khi chuẩn bị rời khỏi nơi quái dị này, Alana – bạn gái của Ryan, bất ngờ bị thế lực hắc ám chiếm giữ. Ryan, Ben và Elsa buộc phải bước vào cuộc chiến nghẹt thở giữa ranh giới của sự sống, cái chết và bóng tối ma mị để giành lại Alana trước khi quá muộn.', 91, '2025-08-01', 'Tiếng Indonesia - Phụ đề tiếng Việt và tiếng Anh', 'Kinh Dị', 'img/ma-long-thong/1080wx608h-longthong.jpg', NULL, 'T18 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 18 TUỔI TRỞ LÊN (18+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('PHIM XÌ TRUM', 'Câu chuyện trở lại với ngôi làng Xì Trum, nơi mà mỗi ngày đều là lễ hội. Bỗng một ngày, sự yên bình của ngôi làng bị phá vỡ khi Tí Vua bị bắt cóc một cách bí ẩn bởi hai phù thủy độc ác Gà Mên và Cà Mên. Từ đây, Tí Cô Nương phải dẫn dắt các Tí đi vào thế giới thực để giải cứu ông. Với sự giúp đỡ của những người bạn mới, các Tí sẽ bước vào cuộc phiêu lưu khám phá định mệnh của mình để cứu lấy vũ trụ.', 92, '2025-07-18', 'Tiếng Anh – Phụ đề, lồng tiếng Việt', 'Gia đình, Hài, Hoạt Hình, Phiêu Lưu', 'img/smurfs-movie/1800x1200-smurfs.jpg', NULL, 'P - PHIM ĐƯỢC PHÉP PHỔ BIẾN ĐẾN NGƯỜI XEM Ở MỌI ĐỘ TUỔI.');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TIẾNG ỒN QUỶ DỊ', 'Sau khi dọn vào căn hộ mới, hai chị em Joo-Young (Lee Sun-Bin) và Joo-Hee (Han Su-A) liên tục bị quấy nhiễu bởi những tiếng động kỳ quái phát ra từ giữa các tầng – thứ âm thanh âm ỉ, vặn vẹo như thể có ai đó… hoặc thứ gì đó đang sống giữa các bức tường. Rồi một ngày, Joo-Hee biến mất không dấu vết. Joo-Young cùng bạn trai của em gái lao vào cuộc truy tìm trong vô vọng – khi càng đào sâu, họ càng tiến gần đến một bí mật đen tối bị chôn vùi sau những bức tường cách âm. Cùng lúc đó, người đàn ông sống tầng dưới cũng bị tra tấn bởi chính thứ âm thanh đó — và tin rằng, con quái vật nằm ngay trong căn hộ của hai chị em.', 94, '2025-07-18', 'Tiếng Hàn – Phụ đề Tiếng Việt và Tiếng Anh', 'Hồi hộp, Kinh Dị', 'img/noise/1080wx608h.jpg', NULL, 'T18 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 18 TUỔI TRỞ LÊN (18+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('SUPERMAN', 'Mùa hè tới đây, Warner Bros. Pictures sẽ mang “Superman” - phim điện ảnh đầu tiên của DC Studios đến các rạp chiếu trên toàn cầu. Với phong cách riêng biệt của mình, James Gunn sẽ khắc họa người hùng huyền thoại trong vũ trụ DC hoàn toàn mới, với sự kết hợp độc đáo của các yếu tố hành động đỉnh cao, hài hước và vô cùng cảm xúc. Một Superman với lòng trắc ẩn và niềm tin vào sự thiện lương của con người sẽ xuất hiện đầy hứa hẹn trên màn ảnh.', 130, '2025-07-11', 'Tiếng Anh - Phụ đề Tiếng Việt & Tiếng Hàn', 'Hành Động, Phiêu Lưu', 'img/superman/1920x1080_up.jpg', NULL, 'T13 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 13 TUỔI TRỞ LÊN (13+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('THÁM TỬ TƯ: PHÍA SAU VẾT MÁU', '5 vụ án mạng rúng động, một "trò chơi" sinh tử đầy uẩn khúc - và Cổ Thiên Lạc, trong vai thám tử tư, vô tình bị cuốn vào vòng xoáy kinh hoàng đó. Bí ẩn nối tiếp bí ẩn, ai sẽ là con mồi, ai mới là kẻ săn? Câu trả lời đang chờ được hé lộ…', 103, '2025-07-25', 'Quảng Đông với Phụ đề Tiếng Anh và Lồng Tiếng Việt; Quảng Đông với phụ đề Tiếng Việt', 'Kinh Dị, Tội phạm', 'img/behind-the-shadow/1080wx650h-bts.jpg', NULL, 'T18 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 18 TUỔI TRỞ LÊN (18+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('THẾ GIỚI KHỦNG LONG: TÁI SINH', 'Thế Giới Khủng Long: Tái Sinh lấy bối cảnh 5 năm sau phần phim Thế Giới Khủng Long: Lãnh Địa, môi trường Trái đất đã chứng tỏ phần lớn là không phù hợp với khủng long. Nhiều loài thằn lằn tiền sử được tái sinh đã chết. Những con chưa chết đã rút lui đến một vùng nhiệt đới hẻo lánh gần phòng thí nghiệm. Địa điểm đó chính là nơi bộ ba Scarlett Johansson, Mahershala Ali và Jonathan Bailey dấn thân vào một nhiệm vụ cực kỳ hiểm nguy.', 134, '2025-07-04', 'Tiếng Anh với phụ đề tiếng Việt', 'Hành Động, Phiêu Lưu, Thần thoại', 'img/jurassic-world-rebirth/jurassic_2025.jpg', NULL, 'T13 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 13 TUỔI TRỞ LÊN (13+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('MỘT NỬA HOÀN HẢO', 'Lucy (Dakota Johnson), một cô gái xinh đẹp làm công việc mai mối ở New York. “Mát tay” trong chuyện mai mối giúp người khác, nhưng trớ trêu Lucy lại “lạc lối” trong câu chuyện tình cảm của chính mình. Bất ngờ đối mặt với ngã rẽ tình cảm khi gặp lại người yêu cũ "không hoàn hảo" (Chris Evans) và một người đàn ông "hoàn hảo" (Pedro Pascal). Cô nàng bị đẩy vào tình thế khó xử. Đứng giữa những lựa chọn cảm xúc đầy mâu thuẫn, lúc này đây, Lucy mới nhận ra việc tìm kiếm tình yêu đích thực chưa bao giờ là điều dễ dàng', 117, '2025-07-04', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Hài, Tâm Lý, Tình cảm', 'img/materialists/350x495-materialists.jpg', NULL, 'T16 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 16 TUỔI TRỞ LÊN (16+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('F1®', 'Sonny Hayes (Brad Pitt) được mệnh danh là "Huyền thoại chưa từng được gọi tên" là ngôi sao sáng giá nhất của FORMULA 1 trong những năm 1990 cho đến khi một vụ tai nạn trên đường đua suýt nữa đã kết thúc sự nghiệp của anh.. Ba mươi năm sau, Sonny trở thành một tay đua tự do, cho đến khi người đồng đội cũ của anh, Ruben Cervantes (Javier Bardem), chủ sở hữu một đội đua F1 đang trên bờ vực sụp đổ, tìm đến anh. Ruben thuyết phục Sonny quay lại với F1® để có một cơ hội cuối cùng cứu lấy đội và khẳng định mình là tay đua xuất sắc nhất thế giới. Anh sẽ thi đấu cùng Joshua Pearce (Damson Idris), tay đua tân binh đầy tham vọng của đội, người luôn muốn tạo ra tốc độ của riêng mình. Tuy nhiên, khi động cơ gầm rú, quá khứ của Sonny sẽ đuổi theo anh và anh nhận ra rằng trong F1, người đồng đội chính là đối thủ cạnh tranh lớn nhất—và con đường chuộc lại lỗi lầm không phải là điều có thể đi một mình.', 156, '2025-06-27', 'Tiếng Anh – phụ đề Tiếng Việt và phụ đề tiếng Hàn', 'Hành Động, Tâm Lý', 'img/f1/poster_f1_6_1_.jpg', NULL, 'T16 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 16 TUỔI TRỞ LÊN (16+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('BÍ KÍP LUYỆN RỒNG', 'Câu chuyện về một chàng trai trẻ với ước mơ trở thành thợ săn rồng, nhưng định mệnh lại đưa đẩy anh đến tình bạn bất ngờ với một chú rồng.', 126, '2025-06-13', 'Tiếng Anh – Phụ đề, lồng tiếng Việt', 'Hài, Hành Động, Phiêu Lưu, Thần thoại', 'img/how-to-train-your-dragon/hdg_digiposter_470x700.jpg', NULL, 'K - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM DƯỚI 13 TUỔI VÀ CÓ NGƯỜI BẢO HỘ ĐI KÈM');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TRẠI TU NUÔI QUỶ', 'Trong một lần trốn tránh cảnh sát, Issa và anh trai Tomas tìm nơi trú ẩn trong một trại trẻ mồ côi hẻo lánh giữa rừng. Nhưng nơi trú ẩn an toàn của hai anh em sớm lộ ra là một địa ngục trần gian do một giáo phái tế thần cai quản, phục tùng một vị thần đáng sợ chuyên hành động về đêm.', 105, '2025-08-15', 'Tiếng Philippines - Phụ đề tiếng Việt & tiếng Anh', 'Kinh Dị', 'img/devil-nun/1080wx650h-devilnun.jpg', NULL, 'T18 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 18 TUỔI TRỞ LÊN (18+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('NGÀY THỨ SÁU SIÊU KỲ QUÁI', 'Sau 22 năm, phim teen đình đám một thời “Freaky Friday” sẵn sàng trở lại với phần tiếp theo mang tên “Freakier Friday”. Giờ đây Anna đã trở thành một người mẹ, cô có một cô con gái tên Harper. Anna cũng có một cô con gái là con riêng của chồng, tên Lily, người sẽ gọi cô là mẹ kế. Lần này, phép màu không chỉ hoán đổi Anna và mẹ Tess, mà hai cô con gái cũng “nhập cuộc”. Cụ thể, Anna hoán đổi cơ thể với Harper, còn mẹ Tess hoán đổi với Lily.', 110, '2025-08-15', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Gia đình, Hài, Thần thoại', 'img/freakier-friday/1200x1800-ff.jpg', NULL, 'T13 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 13 TUỔI TRỞ LÊN (13+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('DÍNH LẸO', 'Dính Lẹo xoay quanh Tim và Millie, một cặp đôi đã gắn bó được 10 năm và tình cảm đang dần phai nhạt. Cố gắng cứu vãn mối quan hệ trên bờ vực đổ vỡ, Tim và Millie chuyển đến sống tại một vùng quê trong lành. Khi đi dạo ở khu rừng gần nhà, họ vô tình uống phải nước từ một chiếc ao kỳ lạ trong hang động. Kể từ đó, cơ thể họ bắt đầu… dính nhau cả về mặt vật lý lẫn tâm lý, theo cách kỳ quái và ngày càng mất kiểm soát. Trên hành trình chống lại hiện tượng kinh dị này, cặp đôi khám phá ra bí mật rúng động phía sau và buộc phải đối mặt với lựa chọn: tình yêu hay sự huỷ diệt về thể xác?', 102, '2025-08-15', 'Tiếng Anh - phụ đề Tiếng Việt', 'Kinh Dị, Tình cảm', 'img/together/tog_main-thumb.jpg', NULL, 'T18 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 18 TUỔI TRỞ LÊN (18+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('KẺ VÔ DANH 2', 'Bob Odenkirk trở lại với vai Hutch Mansell - người chồng, người cha sống ở vùng ngoại ô và cũng là sát thủ nghiện công việc trong chương mới của Nobody, bộ phim hành động - giật gân đầy ly kỳ từng có doanh thu mở màn đứng đầu phòng vé Hoa Kỳ vào năm 2021. Bốn năm sau khi vô tình đụng độ mafia Nga, Hutch vẫn còn nợ 30 triệu đô và đang trả dần bằng chuỗi nhiệm vụ ám sát không hồi kết nhắm vào tội phạm quốc tế. Dù phần nào yêu thích công việc bạo lực này, Hutch và vợ Becca bắt đầu kiệt sức và xa cách, nên quyết định tổ chức chuyến du lịch đến công viên nước để hâm nóng tình cảm gia đình. Tuy nhiên, kỳ nghỉ nhanh chóng rơi vào hỗn loạn khi Hutch đối đầu với thế lực ngầm tàn bạo nhất từ trước đến nay. Hành động nghẹt thở, hài hước đen và những pha đối đầu không khoan nhượng.', 90, '2025-08-15', 'Tiếng Anh - phụ đề Tiếng Việt', 'Hành Động, Hồi hộp', 'img/nobody-2/nb2_poolposter_1800x1200.jpg', NULL, 'T18 - PHIM ĐƯỢC PHỔ BIẾN ĐẾN NGƯỜI XEM TỪ ĐỦ 18 TUỔI TRỞ LÊN (18+)');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TÔI CÓ BỆNH MỚI THÍCH CẬU', 'Giả ung thư để khỏi bị đuổi học, chàng nam sinh cấp 3 Tử Kiệt đâu ngờ rằng mình lại mắc bệnh tương tư Tử Khiết, cô nàng lớp trưởng thông minh, xinh đẹp nhưng lạnh lùng và nghi ngờ cậu đang “diễn sâu.”', NULL, '2025-08-15', 'Tiếng Trung Quốc - Phụ đề Tiếng Việt', 'Hài, Tình cảm', 'img/lovesick/350x495-lovesick.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('PHIM SHIN CẬU BÉ BÚT CHÌ: NÓNG BỎNG TAY! NHỮNG VŨ CÔNG SIÊU CAY KASUKABE', 'Để thiết lập mối quan hệ giữa một thành phố ở Ấn Độ và Kasukabe, Lễ hội Giải trí Thiếu nhi Kasukabe chính thức được tổ chức. Và bất ngờ chưa, ban tổ chức thông báo rằng đội chiến thắng trong cuộc thi nhảy của lễ hội sẽ được mời sang Ấn Độ biểu diễn ngay trên sân khấu bản địa! Nghe vậy, Shin và Đội đặc nhiệm Kasukabe lập tức lên kế hoạch chinh phục giải thưởng và khởi hành sang Ấn Độ để “quẩy banh nóc”! Chuyến du lịch tưởng chừng chỉ có vui chơi ca hát lại rẽ hướng 180 độ khi Shin và Bo tình cờ lạc vào một tiệm tạp hóa bí ẩn giữa lòng Ấn Độ. Tại đây, cả hai bắt gặp một chiếc balo có hình dáng giống... cái mũi và cả hai quyết định mua về. Nhưng không ngờ, chiếc balo lại ẩn chứa một bí mật kỳ lạ. Trong lúc tò mò nghịch ngợm, Bo lỡ tay nhét một mảnh giấy kỳ lạ từ balo lên... mũi mình. Và thế là thảm họa bắt đầu! Một thế lực tà ác trỗi dậy, biến Bo trở thành “Bạo Chúa Bo” – phiên bản siêu tăng động, cực kỳ hung hãn và sở hữu sức mạnh đủ để... làm rung chuyển cả thế giới. Liệu Shin và những người bạn có thể ngăn chặn Bo phiên bản Bạo Chúa trước khi cậu ấy khiến Ấn Độ (và cả thế giới) chìm trong hỗn loạn?', 105, '2025-08-22', 'Lồng tiếng Việt, Phụ đề Anh - Việt', 'Gia đình, Hài, Hoạt Hình, Phiêu Lưu', 'img/shin-kasukabe-dancers/1.20wx1.80h.jpg', NULL, 'P - PHIM ĐƯỢC PHÉP PHỔ BIẾN ĐẾN NGƯỜI XEM Ở MỌI ĐỘ TUỔI.');
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('MƯA ĐỎ', '“Mưa Đỏ” - Phim truyện điện ảnh về đề tài chiến tranh cách mạng, kịch bản của nhà văn Chu Lai, lấy cảm hứng và hư cấu từ sự kiện 81 ngày đêm chiến đấu anh dũng, kiên cường của nhân dân và cán bộ, chiến sĩ bảo vệ Thành Cổ Quảng Trị năm 1972. Tiểu đội 1 gồm toàn những thanh niên trẻ tuổi và đầy nhiệt huyết là một trong những đơn vị chiến đấu, bám trụ tại trận địa khốc liệt này. Bộ phim là khúc tráng ca bằng hình ảnh, là nén tâm nhang tri ân và tưởng nhớ những người con đã dâng hiến tuổi thanh xuân cho đất nước, mang âm hưởng của tình yêu, tình đồng đội thiêng liêng, là khát vọng hòa bình, hoà hợp dân tộc của nhân dân Việt Nam.', NULL, '2025-08-22', 'Tiếng Việt', 'Hành Động, Lịch Sử', 'img/mua-do/350x495-muado.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('XIN CHÀO JADOO: LẠC VÀO VƯƠNG QUỐC PHÉP THUẬT', 'Một ngày nọ, một cô bé tên Jadoo bất ngờ chui ra từ một tảng đá gần ngôi làng. Jadoo mong được sống cùng dân làng, nhưng lại bị đuổi đi vì bị vu oan. Không còn nơi nào để nương tựa, cô tìm đến một võ đường để xin chỗ ở, bắt đầu tập luyện võ thuật và dần dần kết bạn, bộc lộ những kỹ năng phi thường. Khi phát hiện ngôi làng sắp bị quái vật tấn công, Jadoo ban đầu không muốn quay về nơi từng ruồng bỏ mình. Nhưng rồi cô nhớ đến lũ trẻ năm xưa — những đứa trẻ đã mang khoai lang cho cô khi cô đói khát…Liệu như trong truyền thuyết, Jadoo có thực sự trở thành người nắm giữ cây gậy phép, cứu lấy ngôi làng?', NULL, '2025-08-29', 'Tiếng Hàn - Phụ đề Tiếng Việt & Lồng Tiếng Việt', 'Hoạt Hình', 'img/jadoo-hero/1800x1200-jadoo.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('CÔ DÂU MA', 'Đám cưới Việt – Thái hóa nghi thức rợn người và ám ảnh. Lấy cảm hứng từ vụ thảm sát tại một gia tộc bí ẩn ở Thái Lan, Cô Dâu Ma hé lộ những nghi thức và bí mật kinh hoàng đang đón chờ Yến khi lần đầu làm dâu trên đất Thái.', NULL, '2025-08-29', NULL, 'Kinh Dị', 'img/co-dau-ma/c_d_u_ma_-_payoff_poster_-_kc_29082025.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('BĂNG ĐẢNG QUÁI KIỆT 2', 'Biệt đội Bad Guys đang cố gắng lấy lại sự tin tưởng của mọi người sau khi hoàn lương, nhưng mọi nỗ lực sụp đổ khi họ bị kéo vào "phi vụ cuối cùng" do nhóm Bad Girls cầm đầu. Liệu họ sẽ giữ vững lý tưởng chính nghĩa, hay lại bị cuốn vào con đường tội phạm đã từng cố gắng rời bỏ?', NULL, '2025-08-29', 'Tiếng Anh - Phụ đề Tiếng Việt; Lồng tiếng Việt', 'Hài, Hoạt Hình', 'img/bad-guys-2/470x700-badguys2.png', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('LÀM GIÀU VỚI MA 2: CUỘC CHIẾN HỘT XOÀN', 'Hành trình đầy bi hài của 5 con người với những toan tính khác nhau nhằm đưa thi thể minh tinh Anh Thư (Ngọc Xuân) về quê nhà, đổi lại, hồn ma của cô hứa sẽ trả cho họ chiếc nhẫn kim cương trị giá 9 tỷ. Ai sẽ là người giành được phần thưởng, hay chuyến đi sẽ bộc lộ phần xấu xa nhất bên trong họ?', NULL, '2025-08-29', 'Tiếng Việt - Phụ đề Tiếng Anh', 'Hài, Hành Động', 'img/lam-giau-voi-ma-2/350x495-lgvm2.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('EM XINH TINH QUÁI', 'Cô ấy hóa quỷ khi trời rạng sáng! Gil-gu (Ahn Bo-hyun) – một chàng trai trẻ thất nghiệp, sống vật vờ trong căn hộ nhỏ sau khi từ bỏ công việc, bất ngờ bị cuốn hút bởi Sun-ji (Im Yoon-ah), cô hàng xóm xinh đẹp vừa chuyển đến tầng dưới. Một ánh nhìn, một nhịp tim lỡ, anh ngỡ đã tìm thấy tia sáng giữa cuộc sống tẻ nhạt. Nhưng rồi sáng hôm sau, trong thang máy, Gil-gu đối mặt với một Sun-ji hoàn toàn khác – ánh mắt dữ tợn, diện mạo kỳ quái, khiến anh bàng hoàng. Cô gái dịu dàng ban ngày dường như mang theo một mặt tối đáng sợ mỗi khi rạng sáng về. Lần theo những dấu hiệu lạ lùng, Gil-gu tìm đến cha của Sun-ji – ông Jang-su (Seong Dong-il) và phát hiện ra một bí mật kinh hoàng: Sun-ji là người bình thường vào ban ngày, nhưng khi trời sắp sáng… một con quỷ trong cô sẽ tỉnh giấc. Jang-su đề nghị Gil-gu một công việc nguy hiểm: trở thành người giám hộ cho Sun-ji mỗi khi bình minh cận kề. Tình yêu và nỗi sợ đan xen, liệu Gil-gu có thể vượt qua lời nguyền quỷ dữ trước khi mọi thứ vượt khỏi tầm kiểm soát?', NULL, '2025-09-05', 'Tiếng Hàn - Phụ đề tiếng Việt', 'Hài, Kinh Dị, Tình cảm', 'img/pretty-crazy/3.2._thumb_-_teaser_trailer.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('THE CONJURING: NGHI LỄ CUỐI CÙNG', 'Đến với hồi kết của vũ trụ The Conjuring, The Conjuring: Nghi Lễ Cuối Cùng theo chân cặp đôi trừ tà Ed và Lorraine Warrent đối mặt với một thế lực ác quỷ, kẻ đã reo rắc nỗi kinh hoàng cho gia đình Smurl trong suốt hơn một thập kỷ. Bộ phim lần này cũng được dựa trên một trong những vụ án có thật mà gia đình Warren đã trải qua.', NULL, '2025-09-12', 'Tiếng Anh – phụ đề Tiếng Việt', 'Kinh Dị', 'img/conjuring-last-rites/1080wx608h-conjuring.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('KHẾ ƯỚC BÁN DÂU', 'Lấy cảm hứng từ tiểu thuyết tâm linh của nhà văn Thục Linh kể về Nhài - một cô dâu trẻ được gả vào gia tộc họ Vũ và vô tình bị cuốn vào một “khế ước” kinh hoàng với quỷ dữ.', NULL, '2025-09-12', 'Tiếng Việt - Phụ đề Tiếng Anh', 'Bí ẩn, Kinh Dị', 'img/khe-uoc-ban-dau/640x396-kubd.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TỬ CHIẾN TRÊN KHÔNG', 'Tử Chiến Trên Không là phim điện ảnh hành động - kịch tính, được lấy cảm hứng từ vụ cướp máy bay có thật tại Việt Nam sau năm 1975. Đón xem hành động Việt Nam kịch tính nhất tháng 9 này!', NULL, '2025-09-19', 'Tiếng Việt', 'Hành Động, Hồi hộp', 'img/tu-chien-tren-khong/tctk_thumb1stlook_16x9.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TRĂM DẶM TỬ THẦN', 'Dựa trên cuốn tiểu thuyết cùng tên của bậc thầy kể chuyện Stephen King, Trăm Dặm Tử Thần xoay quanh một nhóm thiếu niên tham gia một cuộc thi thường niên mang tên chuyến Đi Xa. Tại đây, họ sẽ phải duy trì một tốc độ nhất định, nếu không, họ sẽ bị loại khỏi cuộc chơi, vĩnh viễn.', NULL, '2025-09-19', 'Tiếng Anh – Phụ đề Tiếng Việt', 'Kinh Dị', 'img/long-walk/poster_tram_dam_tu_than_1.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TRẬN CHIẾN SAU TRẬN CHIẾN', 'Mười sáu năm sau khi kẻ thù tàn ác biến mất, nhóm cựu chiến sĩ cách mạng buộc phải tái hợp để cứu con gái của một người anh em trong nhóm.', NULL, '2025-09-26', 'Tiếng Anh – phụ đề Tiếng Việt', 'Hành Động, Hồi hộp, Tâm Lý', 'img/one-battle-after-another/vn_obaa_vert_tsr_leo_2764x4096_intl.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('NHÀ BÚP BÊ CỦA GABBY: PHIM ĐIỆN ẢNH', 'Thương hiệu của loạt phim hoạt hình ăn khách toàn cầu từ nhà DreamWorks Animation nay được nâng cấp lên thành cuộc phiêu lưu trên màn ảnh rộng. Bộ phim xoay quanh câu chuyện Gabby cùng bà ngoại Gigi lên đường đến xứ sở thần tiên đô thị Cat Francisco. Nhưng khi ngôi nhà búp bê của Gabby, vật sở hữu quý giá nhất của cô, rơi vào tay một bà mèo lập dị tên Vera, Gabby bắt đầu cuộc phiêu lưu vào thế giới thực để đoàn tụ với những chú mèo Gabby và cứu ngôi nhà búp bê trước khi quá muộn.', NULL, '2025-09-26', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Hoạt Hình, Phiêu Lưu', 'img/gabby-doll-house/gdh_window1sheet_1800x1200.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TRON: ARES', 'Không còn đường lùi. Tron: Ares dự kiến khởi chiếu 10.10.25.', NULL, '2025-10-10', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Hành Động, Khoa Học Viễn Tưởng, Phiêu Lưu', 'img/tron-ares/tron_ares_-_cgv.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('NĂM CỦA ANH, NGÀY CỦA EM', 'Khi thế giới bị chia cắt thành 2 chiều không gian song song, tình yêu nảy nở giữa Hứa Quang Hán và Viên Lễ Lâm bị cuốn trôi theo hai nhịp sống khác biệt. Họ vẫn níu giữ sợi dây mong manh của định mệnh, cố tìm đến điểm giao nhau giữa hai thế giới để viết tiếp chuyện tình còn dang dở. Một bản tình ca lặng lẽ và day dứt, nơi Hứa Quang Hán nhẹ nhàng mang đến những nốt lặng bồi hồi, liệu tình yêu có đủ để vượt qua giới hạn của không gian và thời gian?', NULL, NULL, 'Tiếng Trung Quốc - Phụ đề Tiếng Việt', 'Thần thoại, Tình cảm', 'img/measure-in-love/406x600-hqh.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('ĐIỆN THOẠI ĐEN 2', 'Bốn năm trước, Finn khi mới 13 tuổi đã giết chết kẻ bắt cóc mình và trốn thoát thành công, trở thành người duy nhất sống sót sau vụ án của The Grabber - một kẻ sát nhân khét tiếng. Nhưng cái ác thực sự không chết đi… và chiếc điện thoại đen lại một lần nữa đổ chuông. Khi Finn nay đã 17 tuổi và vẫn vật lộn với hậu chấn tâm lý sau vụ bắt cóc, còn cô em gái 15 tuổi Gwen bắt đầu nhận được các cuộc gọi trong giấc mơ từ chiếc điện thoại đen kỳ lạ, cùng với những hình ảnh rùng rợn. Quyết tâm khám phá sự thật và chấm dứt cơn ác mộng cho cả bản thân lẫn anh trai, Gwen thuyết phục Finn cùng đến trại trong lúc có bão tuyết. Tại đây, cô phát hiện ra một bí mật kinh hoàng liên quan đến The Grabber và cả quá khứ của gia đình mình. Giờ đây, 2 anh em buộc phải đối đầu với một kẻ sát nhân không chỉ mạnh mẽ hơn sau khi chết, mà còn có mối liên hệ sâu sắc với họ hơn những gì họ từng tưởng tượng.', NULL, '2025-10-17', 'Tiếng Anh - phụ đề Tiếng Việt', 'Hồi hộp, Kinh Dị', 'img/black-phone-2/bp2_teaser_1800x1200.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('TRỐN CHẠY TỬ THẦN', 'Trong bối cảnh xã hội tương lai gần, Trốn Chạy Tử Thần là chương trình truyền hình ăn khách nhất, một cuộc thi sinh tồn khốc liệt nơi các thí sinh, được gọi là “Runners”, phải trốn chạy suốt 30 ngày khỏi sự truy đuổi của các sát thủ chuyên nghiệp. Mọi bước đi của họ đều được phát sóng công khai cho khán giả theo dõi và phần thưởng tiền mặt sẽ tăng lên sau mỗi ngày sống sót. Vì cần tiền cứu chữa cho cô con gái bệnh nặng, Ben Richards (do Glen Powell thủ vai), một người lao động nghèo, chấp nhận lời mời từ Dan Killian (do Josh Brolin thủ vai), nhà sản xuất chương trình bảnh bao nhưng tàn nhẫn, để tham gia cuộc chơi như một lựa chọn cuối cùng. Tuy nhiên, sự gan lì, nhạy bén và ý chí sinh tồn mãnh liệt của Ben lại khiến anh bất ngờ trở thành nhân vật được khán giả yêu thích nhất và là mối đe dọa với cả hệ thống. Khi lượng người xem tăng vọt, hiểm nguy cũng ngày càng bủa vây. Giờ đây, Ben không chỉ phải đối mặt với toán sát thủ mà còn cả một đất nước đang nghiện cảm giác chứng kiến anh gục ngã.', NULL, '2025-11-07', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Khoa Học Viễn Tưởng, Phiêu Lưu', 'img/running-man/trm_teaser_1800x1200.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('MORTAL KOMBAT: CUỘC CHIẾN SINH TỬ II', 'Hãng phim New Line Cinema, phần tiếp theo đầy kịch tính trong loạt phim bom tấn chuyển thể từ trò chơi điện tử đình đám – Mortal Kombat II – trở lại với tất cả sự tàn bạo vốn có. Lần này, những nhà vô địch được yêu thích – nay có sự góp mặt của chính Johnny Cage – sẽ đối đầu với nhau trong trận chiến đẫm máu, không khoan nhượng, nhằm đánh bại thế lực đen tối của Shao Kahn đang đe dọa đến sự tồn vong của Earthrealm và các chiến binh bảo vệ nó.', NULL, '2025-10-24', 'Tiếng Anh – Phụ đề Tiếng Việt', 'Hành Động, Phiêu Lưu, Thần thoại', 'img/mortal-kombat-ii/vn_kombat2_vert_tsr_2764x4096_intl.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('WICKED: PHẦN 2', 'Bộ phim chuyển thể từ sân khấu Broadway thành công nhất mọi thời đại, hiện tượng điện ảnh toàn cầu năm 2024 nay chính thức đi đến hồi kết hoành tráng, kịch tính và đầy cảm xúc trong Wicked: Phần 2. Chương cuối của câu chuyện bắt đầu khi Elphaba và Glinda đã xa cách, mỗi người đang sống với hậu quả từ những lựa chọn của riêng mình. Elphaba giờ đây bị cả xứ Oz xem như Phù thủy độc ác phương Tây, trong khi đó Glinda đã trở thành biểu tượng quyến rũ của Lòng tốt đối với toàn bộ xứ Oz. Và mọi thứ thay đổi khi một cô gái đến từ Kansas bất ngờ xuất hiện, khiến cuộc sống của xứ Oz bị đảo lộn. Glinda và Elphaba buộc phải cùng nhau đối mặt lần cuối, họ phải thật sự thấu hiểu nhau, bằng sự trung thực và đồng cảm, nếu muốn thay đổi chính mình và cả vận mệnh của xứ Oz. Liệu Elphaba và Glinda có thể vượt qua số phận đã được định sẵn để viết lại tương lai cho xứ sở phép màu này?', NULL, '2025-11-21', 'Tiếng Anh - phụ đề Tiếng Việt', 'Nhạc kịch, Thần thoại', 'img/wicked-for-good/wk2_cliffposter_1800x1200.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('PHI VỤ ĐỘNG TRỜI 2', 'ZOOTOPIA 2 trở lại sau 9 năm Đu OTP Nick & Judy chuẩn bị 28.11.2025 này ra rạp nhé', NULL, '2025-11-28', NULL, 'Gia đình, Hành Động, Phiêu Lưu, Thần thoại', 'img/zootopia-2/teaser2_poster_vietnam.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('CUỘC TÌNH VỤNG TRỘM', '“Nếu cuộc sống không như ý em, tôi theo ý em. Nếu may mắn không ở bên em, tôi ở bên em. Em hãy tin rằng, sẽ luôn có người vì em mà đến, hiểu những điều trong lòng em, biết những đau khổ của em, ở bên em cùng em đi qua mưa gió một đời." – trích cre sưu tầm. Xem ngay Trailer Chính Thức bộ phim tình nhất tháng 12 năm nay, Regretting You – Cuộc Tình Vụng Trộm tại đây nha', 114, '2025-12-05', 'Phụ đề tiếng Việt & Lồng tiếng Việt', 'Tâm Lý, Tình cảm', 'img/regretting-you/regretting_you_-_cu_c_t_nh_v_ng_tr_m.jpg', NULL, NULL);
