INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('SPONGEBOB: LỜI NGUYỀN HẢI TẶC', 'SpongeBob phiêu lưu xuống đáy đại dương để đối mặt với hồn ma của Người Hà Lan bay, vượt qua thử thách và khám phá những bí ẩn dưới biển.', NULL, '2025-12-26', 'Tiếng Anh - Phụ đề Tiếng Việt & Lồng Tiếng Việt', 'Hài, Hoạt Hình, Phiêu Lưu', 'img/search-for-squarepants/1920x1080-sp_1.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('CHÀNG MÈO MANG MŨ', 'Trong phim,Chàng Mèo Mang Mũ của chúng ta nhận nhiệm vụ khó khăn nhất từ trước đến nay từ tổ chức I.I.I.I. (Viện Sáng Tạo và Cảm Hứng, LLC): cổ vũ tinh thần cho Gabby và Sebastian, hai anh em đang vật lộn với việc thích nghi sau khi chuyển đến một thị trấn mới. Với "thành tích" thường xuyên làm quá mọi chuyện, lần này có thể sẽ là cơ hội cuối cùng để anh chàng chuyên gây rối này chứng minh bản thân nếu không muốn mất đi chiếc mũ thần kỳ!', NULL, '2026-02-27', 'Tiếng Anh với phụ đề tiếng Việt', 'Gia đình, Hài, Hoạt Hình, Phiêu Lưu', 'img/the-cat-in-the-hat/thecatinthehat_poster.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('THOÁT KHỎI TẬN THẾ', 'Ryland Grace (Ryan Gosling) tỉnh dậy trong một con tàu vũ trụ mà không hề có bất kỳ ký ức gì. Anh dần khám phá ra mình là thành viên duy nhất còn sống sót của dự án Hail Mary - một sứ mệnh táo bạo đưa con tàu đến hệ mặt trời Tau Ceti để tìm cách cứu Trái đất khỏi ngày tận thế. Bất ngờ khi Grace đối mặt với một con tàu lạ và gặp gỡ sinh vật ngoài hành tinh mà anh đặt tên là Rocky.', NULL, '2026-03-20', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Khoa Học Viễn Tưởng, Phiêu Lưu', 'img/project-hail-mary/phm_intl_online_1080x1350_03.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('CÚ NHẢY KỲ DIỆU', 'Nó tưng tửng mà nó dễ thương thiệt sự luôn. Ai từng mê nét hài của ba anh gấu trong We Bare Bears thì chuẩn bị tinh thần nha, Hoppers là cả một… rừng thú tấu hài, từ cùng đạo diễn đứng sau loạt phim gấu đó. Hoppers (Cú Nhảy Kỳ Diệu) dự kiến ra rạp vào tháng 3 năm 2026', NULL, '2026-03-26', NULL, 'Gia đình, Hài, Hoạt Hình, Phiêu Lưu', 'img/hoppers/hoppers_-_poster.jpg', NULL, NULL);
INSERT INTO movies (title, description, duration, release_date, language, genre, poster_url, trailer_url, rating) VALUES ('THE ODYSSEY', 'The Odyssey lấy cảm hứng từ trường ca của Homer, ghi hình hoàn toàn bằng máy quay phim Imax, có kinh phí ước tính 250 triệu USD. Tác phẩm lấy cảm hứng từ thần thoại Hy Lạp, trong đó Matt Damon vào vai Odysseus, người anh hùng mất 10 năm trở về quê nhà sau cuộc chiến thành Troy. Nhân vật chạm trán với các vị thần, quái vật, trải qua nhiều thử thách để đoàn tụ vợ và giành lại vương quốc.', NULL, '2026-07-17', 'Tiếng Anh - Phụ đề Tiếng Việt', 'Hành Động, Thần thoại', 'img/the-odyssey/ody_teaser_1800x1200.jpg', NULL, NULL);


DROP TABLE IF EXISTS screens CASCADE;
-- 4. Screens
CREATE TABLE screens (
    screen_id SERIAL PRIMARY KEY,
    cinema_id INT REFERENCES cinemas(cinema_id) ON DELETE CASCADE,
    name VARCHAR NOT NULL,
    capacity INT
);

DROP TABLE IF EXISTS showtimes CASCADE;
-- 5. Showtimes
CREATE TABLE showtimes (
    showtime_id SERIAL PRIMARY KEY,
    movie_id INT REFERENCES movies(movie_id) ON DELETE CASCADE,
    cinema_id INT REFERENCES cinemas(cinema_id) ON DELETE CASCADE,
    screen_id INT REFERENCES screens(screen_id) ON DELETE SET NULL,
    start_time TIMESTAMP NOT NULL,
    price DECIMAL(10,2)
);

DROP TABLE IF EXISTS seats CASCADE;
-- 6. Seats
CREATE TABLE seats (
    seat_id SERIAL PRIMARY KEY,
    screen_id INT REFERENCES screens(screen_id) ON DELETE CASCADE,
    row VARCHAR NOT NULL,
    number INT NOT NULL,
    type VARCHAR
);

DROP TABLE IF EXISTS bookings CASCADE;
-- 7. Bookings
CREATE TABLE bookings (
    booking_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES users(user_id) ON DELETE CASCADE,
    showtime_id INT REFERENCES showtimes(showtime_id) ON DELETE CASCADE,
    seats JSONB,
    total_amount DECIMAL(10,2),
    booking_date TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
    status VARCHAR CHECK (status IN ('pending', 'paid', 'cancelled')) DEFAULT 'pending'
);
